locals {
  mycidrs = [for cidr in data.aws_vpc_ipam_pool_cidrs.c.ipam_pool_cidrs :
    cidr.cidr if
  cidr.state == "provisioned"]
}

resource "aws_ec2_managed_prefix_list" "pls" {
  name           = "IPAM Pool (${aws_vpc_ipam_pool.test.id}) Cidrs"
