resource "aws_kinesis_firehose_delivery_stream" "example" {
  name        = "example-firehose-delivery-stream"
  destination = "opensearch"

  opensearch_configuration {
    role_arn              = aws_iam_role.firehose_role.arn
    domain_arn            = "arn:aws:opensearch:${data.aws_region.current.name}:${data.aws_caller_identity.current.account_id}:domain/example-opensearch-domain"
    index_name            = "example-index"
    type_name             = "_doc"
    index_rotation_period = "NoRotation"
    buffering_size        = 5
    buffering_interval    = 300
    retry_duration        = 300
    s3_backup_mode        = "FailedDocumentsOnly"
    s3_configuration {
      role_arn   = aws_iam_role.firehose_role.arn
      bucket_arn = aws_s3_bucket.firehose_backup.arn
    }
  }

  server_side_encryption {
    enabled = true
    key_arn = aws_kms_key.firehose_key.arn
  }
}

