resource "aws_route53_record" "us" {
  zone_id = aws_route53_zone.primary.zone_id
  name    = "primary.example.com"
  type    = "A"

  alias {
    name                   = aws_elb.us.dns_name
    zone_id                = aws_elb.us.zone_id
    evaluate_target_health = true
  }

  geolocation_routing_policy {
    continent = "US"
  }

  set_identifier = "us"
}

