resource "aws_s3_bucket_policy" "my_unique_bucket_policy" {
  bucket = aws_s3_bucket.my_unique_bucket.id

  policy = jsonencode({
    Version = "2012-10-17"
    Statement = [
      {
        Sid       = "AllowGetObjectBasedOnReferer"
        Effect    = "Allow"
        Principal = "*"
        Action    = "s3:GetObject"
        Resource  = "arn:aws:s3:::my_unique_bucket_name/*"
        Condition = {
          StringLike = {
            "aws:Referer" = "https://example.com/*"
          }
        }
      }
    ]
  })
}

resource "aws_s3_bucket_server_side_encryption_configuration" "my_unique_bucket_encryption" {
  bucket = aws_s3_bucket.my_unique_bucket.id

  rule {
    apply_server_side_encryption_by_default {
      sse_algorithm = "AES256"
    }
  }
}

