provider "aws" {
  region = "us-west-2"
}

resource "aws_ami" "latest_amazon_linux_2" {
  name                = "latest-amazon-linux-2"
  virtualization_type = "hvm"
  root_device_name    = "/dev/xvda"

  ebs_block_device {
    device_name = "/dev/xvda"
    volume_size = 8
    volume_type = "gp2"
    encrypted   = true
  }

  tags = {
    Name = "latest-amazon-linux-2"
  }

  lifecycle {
    ignore_changes = [ebs_block_device]
  }
}

