resource "aws_s3_bucket_policy" "example" {
  bucket = aws_s3_bucket.example.id

  policy = jsonencode({
    Version = "2012-10-17"
    Statement = [
      {
        Effect    = "Deny"
        Principal = "*"
        Action    = "s3:*"
        Resource = [
          "arn:aws:s3:::${aws_s3_bucket.example.id}",
          "arn:aws:s3:::${aws_s3_bucket.example.id}/*"
        ]
        Condition = {
          StringNotEquals = {
            "aws:sourceArn" : "arn:aws:cloudtrail:${data.aws_region.current.name}:${data.aws_caller_identity.current.account_id}:trail/example-trail"
          }
        }
      }
    ]
  })
}

