          "dynamodb:UpdateItem",
          "dynamodb:DeleteItem"
        ]
        Resource = [
          "arn:aws:dynamodb:${data.aws_region.current.name}:${data.aws_caller_identity.current.account_id}:table/example-table-us-east-1",
          "arn:aws:dynamodb:us-west-1:${data.aws_caller_identity.current.account_id}:table/example-table-us-west-1",
